<style>
.icon-button {
  /* border: 1px solid red; */
  padding: 16px;
  border-radius: 50%;
  position: relative;
  width: 52px;
  height: 52px;

  transition: background-color 0.3s ease-in-out;

  &:hover {
    cursor: pointer;
    background-color: var(--hover);
  }

  svg {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    path {
      fill: var(--color-text);
    }
  }
}
</style>
